// src/components/ChatRoom.js
import React, { useEffect, useRef, useState } from "react";
import socket from "../socket";

export default function ChatRoom({ room, username, onLeave }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  useEffect(() => {
    socket.emit("joinRoom", room);

    const handleMessage = (msg) => setMessages((prev) => [...prev, msg]);
    const handleHistory = (history) => setMessages(history);

    socket.on("message", handleMessage);
    socket.on("chatHistory", handleHistory);

    return () => {
      socket.emit("leaveRoom", room);
      socket.off("message", handleMessage);
      socket.off("chatHistory", handleHistory);
    };
  }, [room]);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = () => {
    if (!input.trim()) return;
    socket.emit("sendMessage", { room, content: input });
    setInput("");
  };

  return (
    <div className="max-w-xl mx-auto p-4 bg-white shadow-md rounded-lg">
      <h2 className="text-xl font-bold mb-4">Room: {room}</h2>

      <div className="h-72 overflow-y-auto border border-gray-300 rounded p-2 bg-gray-50 mb-4">
        {messages.length === 0 && <p className="text-gray-500 italic">No messages yet.</p>}
        {messages.map((m, i) => (
          <div key={i} className="mb-1">
            <span className="font-semibold text-blue-600">{m.sender}</span>: {m.content}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="flex gap-2">
        <input
          type="text"
          className="flex-1 border border-gray-300 rounded px-3 py-1 focus:outline-none focus:ring focus:border-blue-400"
          placeholder="Type your message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        />
        <button
          onClick={sendMessage}
          disabled={!input.trim()}
          className="bg-blue-600 text-white px-4 py-1 rounded hover:bg-blue-700 disabled:opacity-50"
        >
          Send
        </button>
      </div>

      <button
        onClick={onLeave}
        className="mt-4 text-red-600 hover:underline"
      >
        Leave Room
      </button>
    </div>
  );
}
