// src/components/ChatRoom.js
import React, { useEffect, useState } from "react";
import socket from "../socket";

export default function ChatRoom({ room, username, onLeave }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  useEffect(() => {
    socket.emit("joinRoom", room);

    socket.on("message", (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      socket.emit("leaveRoom", room); // Optional, if you handle leaving on backend
      socket.off("message");
    };
  }, [room]);

  const sendMessage = () => {
    if (!input.trim()) return;
    socket.emit("sendMessage", { room, message: input, sender: username });
    setInput("");
  };

  return (
    <div>
      <h2>Room: {room}</h2>
      <div style={{ height: "300px", overflowY: "auto", border: "1px solid black", padding: "5px", marginBottom: "10px" }}>
        {messages.map((m, i) => (
          <div key={i}>
            <b>{m.sender}</b>: {m.text}
          </div>
        ))}
      </div>

      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        placeholder="Type your message..."
      />
      <button onClick={sendMessage}>Send</button>

      <br />
      <button onClick={onLeave} style={{ marginTop: "10px" }}>
        Leave Room
      </button>
    </div>
  );
}
