// src/components/ChatSelector.js
import React, { useState } from "react";
import ChatRoom from "./ChatRoom";
import PrivateChat from "./PrivateChat";

export default function ChatSelector({ username }) {
  const [currentRoom, setCurrentRoom] = useState(null);
  const [privateChatUser, setPrivateChatUser] = useState(null);

  const rooms = ["General", "Sports", "Music"];
  const users = ["Alice", "Bob", "Charlie"];

  if (currentRoom) {
    return (
      <ChatRoom room={currentRoom} username={username} onLeave={() => setCurrentRoom(null)} />
    );
  }

  if (privateChatUser) {
    return (
      <PrivateChat user1={username} user2={privateChatUser} onClose={() => setPrivateChatUser(null)} />
    );
  }

  return (
    <div className="max-w-md mx-auto p-6 bg-white shadow-md rounded-lg">
      <h3 className="text-lg font-bold mb-2">Join a Chat Room</h3>
      <div className="flex flex-wrap gap-2 mb-4">
        {rooms.map((r) => (
          <button
            key={r}
            onClick={() => setCurrentRoom(r)}
            className="bg-green-600 text-white px-4 py-1 rounded hover:bg-green-700"
          >
            {r}
          </button>
        ))}
      </div>

      <h3 className="text-lg font-bold mb-2">Or Chat Privately</h3>
      <div className="flex flex-wrap gap-2">
        {users.filter((u) => u !== username).map((u) => (
          <button
            key={u}
            onClick={() => setPrivateChatUser(u)}
            className="bg-purple-600 text-white px-4 py-1 rounded hover:bg-purple-700"
          >
            {u}
          </button>
        ))}
      </div>
    </div>
  );
}
