// src/components/PrivateChat.js
import React, { useEffect, useState } from "react";
import socket from "../socket";

export default function PrivateChat({ user1, user2, onClose }) {
  const room = [user1, user2].sort().join("_"); // Unique private room name
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  useEffect(() => {
    socket.emit("joinPrivateChat", room);

    socket.on("privateMessage", (msg) => {
      setMessages((prev) => [...prev, msg]);
    });

    return () => {
      socket.emit("leavePrivateChat", room); // Optional
      socket.off("privateMessage");
    };
  }, [room]);

  const sendMessage = () => {
    if (!input.trim()) return;
    socket.emit("sendPrivateMessage", { room, message: input, sender: user1 });
    setInput("");
  };

  return (
    <div>
      <h2>Private chat: {user1} & {user2}</h2>
      <div style={{ height: "300px", overflowY: "auto", border: "1px solid black", padding: "5px", marginBottom: "10px" }}>
        {messages.map((m, i) => (
          <div key={i}>
            <b>{m.sender}</b>: {m.text}
          </div>
        ))}
      </div>

      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        placeholder="Type your message..."
      />
      <button onClick={sendMessage}>Send</button>

      <br />
      <button onClick={onClose} style={{ marginTop: "10px" }}>
        Close Chat
      </button>
    </div>
  );
}
