import React, { useEffect, useState } from "react";
import socket from "../socket";

export default function PrivateChat({ room, username, otherUser, onClose }) {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  useEffect(() => {
    // Join private chat room
    if (room) {
      socket.emit("joinRoom", room);
    }

    // Receive chat history on joining
    const handleChatHistory = (history) => {
      setMessages(history);
    };

    // Receive new messages
    const handleMessage = (msg) => {
      setMessages((prev) => [...prev, msg]);
    };

    socket.on("chatHistory", handleChatHistory);
    socket.on("privateMessage", handleMessage);

    return () => {
      if (room) {
        socket.emit("leaveRoom", room);
      }
      socket.off("chatHistory", handleChatHistory);
      socket.off("privateMessage", handleMessage);
    };
  }, [room]);

  const sendMessage = () => {
    if (!input.trim()) return;
    socket.emit("sendPrivateMessage", { room, content: input });
    setInput("");
  };

  return (
    <div>
      <h2>Private chat: {username} & {otherUser}</h2>
      <div
        style={{
          height: 300,
          overflowY: "auto",
          border: "1px solid black",
          padding: 10,
          marginBottom: 10,
          backgroundColor: "#f9f9f9",
        }}
      >
        {messages.length === 0 && <p>No messages yet.</p>}
        {messages.map((m, i) => (
          <div key={i} style={{ marginBottom: 5 }}>
            <b>{m.sender}:</b> {m.content}
          </div>
        ))}
      </div>

      <input
        type="text"
        value={input}
        placeholder="Type your message..."
        onChange={(e) => setInput(e.target.value)}
        onKeyDown={(e) => e.key === "Enter" && sendMessage()}
        style={{ width: "70%", padding: 8 }}
      />
      <button onClick={sendMessage} style={{ padding: "8px 12px", marginLeft: 5 }}>
        Send
      </button>

      <br />
      <button onClick={onClose} style={{ marginTop: 10 }}>
        Close Chat
      </button>
    </div>
  );
}
